Change_Percentage_2_Forecast.query:
q = load "${App.Datasets.MFG_SalesAgreementProductSchedule_tp.FullyQualifiedName}";
q = filter q by 'isValidProductSchedule' == "true";
 q = foreach q generate 'Id' as 'Id','SalesAgreementProduct.SalesAgreement.Account.Name' as 'SalesAgreementProduct.SalesAgreement.Account.Name', 'StartDate' as 'StartDate', 'ActualEndDate' as 'ActualEndDate', 'ForecastedAmountFinal' as 'ForecastedAmountFinal', 'ForecastedDailyAmount' as 'ForecastedDailyAmount';
 q_current = foreach q generate 'Id' as 'Id','SalesAgreementProduct.SalesAgreement.Account.Name' as 'SalesAgreementProduct.SalesAgreement.Account.Name', 'StartDate' as 'StartDate', 'ActualEndDate' as 'ActualEndDate', {{row(Time_Toggle_1.selection, [0], ["change_percentage_curr_forecasted"]).asObject()}};
 q_previous = foreach q generate 'Id' as 'Id','SalesAgreementProduct.SalesAgreement.Account.Name' as 'SalesAgreementProduct.SalesAgreement.Account.Name', 'StartDate' as 'StartDate', 'ActualEndDate' as 'ActualEndDate', {{row(Time_Toggle_1.selection, [0], ["change_percentage_prev_forecasted"]).asObject()}};
result = group q_current by all full, q_previous by all;
result = foreach result generate coalesce(sum(q_current.'Forecasted_Amount'),0) as 'current_Forecasted_Amount', coalesce(sum(q_previous.'Forecasted_Amount'),0) as 'previous_Forecasted_Amount';
result = foreach result generate ('current_Forecasted_Amount'-'previous_Forecasted_Amount')*100/'previous_Forecasted_Amount' as 'change_percentage';
result = order result by ('change_percentage' asc);

