Change_Percentage_3.query:
q = load "${App.Datasets.MFG_SalesAgreementProductSchedule_tp.FullyQualifiedName}";
q = filter q by 'isValidProductSchedule' == "true";
 q = foreach q generate 'Id' as 'Id','SalesAgreementProduct.SalesAgreement.Account.Name' as 'SalesAgreementProduct.SalesAgreement.Account.Name', 'StartDate' as 'StartDate', 'ActualEndDate' as 'ActualEndDate', (case when 'ActualEndDate_sec_epoch'<date_to_epoch(now()) or 'StartDate_sec_epoch'>date_to_epoch(now()) then 'ActualEndDate' else toString(now(),"yyyy-MM-dd") end) as 'LastValidDate', 'DerivedActualAmount' as 'DerivedActualAmount', 'DerivedPlannedAmount' as 'DerivedPlannedAmount', (case when 'ActualEndDate_sec_epoch'<date_to_epoch(now()) then 'DerivedActualAmount'/(daysBetween(toDate('StartDate', "yyyy-MM-dd"), toDate('ActualEndDate', "yyyy-MM-dd"))+1) else 'DerivedActualAmount'/(daysBetween(toDate('StartDate', "yyyy-MM-dd"), toDate(toString(now(),"yyyy-MM-dd"), "yyyy-MM-dd"))+1) end) as 'ActualDailyAmount', 'PlannedAmountFinal' as 'PlannedAmountFinal', 'PlannedDailyAmount' as 'PlannedDailyAmount';
 q_current = foreach q generate 'Id' as 'Id','SalesAgreementProduct.SalesAgreement.Account.Name' as 'SalesAgreementProduct.SalesAgreement.Account.Name', 'StartDate' as 'StartDate', 'ActualEndDate' as 'ActualEndDate', {{row(Time_Toggle_1.selection, [0], ["change_percentage_curr_planned"]).asObject()}}, {{row(Time_Toggle_1.selection, [0], ["change_percentage_curr_actuals"]).asObject()}};

q_previous = foreach q generate 'Id' as 'Id','SalesAgreementProduct.SalesAgreement.Account.Name' as 'SalesAgreementProduct.SalesAgreement.Account.Name', 'StartDate' as 'StartDate', 'ActualEndDate' as 'ActualEndDate', {{row(Time_Toggle_1.selection, [0], ["change_percentage_prev_planned"]).asObject()}}, {{row(Time_Toggle_1.selection, [0], ["change_percentage_prev_actuals"]).asObject()}};

result = group q_current by all full, q_previous by all;
result = foreach result generate coalesce(sum(q_current.'Planned_Amount'),0) as 'current_Planned_Amount', coalesce(sum(q_previous.'Planned_Amount'),0) as 'previous_Planned_Amount', coalesce(sum(q_current.'Actual_Amount'),0) as 'current_Actual_Amount', coalesce(sum(q_previous.'Actual_Amount'),0) as 'previous_Actual_Amount', round(coalesce(sum(q_current.'Actual_Amount'),0)*100/sum(q_current.'Planned_Amount')) as 'current_Revenue_Realisation',  round(coalesce(sum(q_previous.'Actual_Amount'),0)*100/sum(q_previous.'Planned_Amount')) as 'previous_Revenue_Realisation';
result = foreach result generate round((coalesce(sum('current_Revenue_Realisation'),0)-sum('previous_Revenue_Realisation'))*100/sum('previous_Revenue_Realisation')) as 'Revenue_Realisation_change';
result = order result by ('Revenue_Realisation_change' asc);

Revenue_Realisation_Trend_Forecast.query:
all_slaps = load "${App.Datasets.MFG_SalesAgreementProductSchedule_tp.FullyQualifiedName}";
 all_slaps = filter all_slaps by 'isValidProductSchedule' == "true";
 valid_slaps = filter all_slaps by date('StartDate_Year', 'StartDate_Month', 'StartDate_Day') in [.."current day"];
valid_slaps = filter valid_slaps by date('ActualEndDate_Year', 'ActualEndDate_Month', 'ActualEndDate_Day') in ["1 year ago"..];
valid_slaps = foreach valid_slaps generate date_to_epoch(toDate(number_to_string(string_to_number(toString(now(),"yyyy"))-1, "#")    +    "-01-01 00:00:00", "yyyy-MM-dd HH:mm:ss")) as 'Start', date_to_epoch(toDate(toString(now(),"yyyy-MM-dd")    +    " 23:59:59", "yyyy-MM-dd HH:mm:ss")) as 'End', 'Id' as 'Id', 'StartDate_sec_epoch' as 'StartDate_sec_epoch', 'ActualEndDate_sec_epoch' as 'ActualEndDate_sec_epoch', (case when 'ActualEndDate_sec_epoch'<date_to_epoch(now()) or 'StartDate_sec_epoch'>date_to_epoch(now()) then 'ActualEndDate_sec_epoch' else date_to_epoch(now()) end) as 'LastValidDate_sec_epoch', 'DerivedActualAmount' as 'DerivedActualAmount', 'ForecastedAmountFinal' as 'ForecastedAmountFinal', (case when 'ActualEndDate_sec_epoch'<date_to_epoch(now()) then 'DerivedActualAmount'/(daysBetween(toDate('StartDate', "yyyy-MM-dd"), toDate('ActualEndDate', "yyyy-MM-dd"))+1) else 'DerivedActualAmount'/(daysBetween(toDate('StartDate', "yyyy-MM-dd"), toDate(toString(now(),"yyyy-MM-dd"), "yyyy-MM-dd"))+1) end) as 'ActualDailyAmount', 'ForecastedDailyAmount' as 'ForecastedDailyAmount';
Start = foreach valid_slaps generate 'Start' as 'Start', 'End' as 'End', 'Id' as 'Id', 'StartDate_sec_epoch' as 'Start Date', 'LastValidDate_sec_epoch' as 'End Date', case when ('StartDate_sec_epoch' >= 'Start') then date_to_string(toDate('StartDate_sec_epoch'),"yyyy") else date_to_string(toDate('Start'),"yyyy") end as 'Date_Year', case when ('StartDate_sec_epoch' >= 'Start') then date_to_string(toDate('StartDate_sec_epoch'),"MM") else date_to_string(toDate('Start'),"MM") end as 'Date_Month', 
case when ('StartDate_sec_epoch' >= 'Start') && ('LastValidDate_sec_epoch' > date_to_epoch(month_last_day(toDate('StartDate_sec_epoch')))) then (daysBetween(toDate('StartDate_sec_epoch'),month_last_day(toDate('StartDate_sec_epoch')))+1) * 'ForecastedDailyAmount' when ('StartDate_sec_epoch' < 'Start') && ('LastValidDate_sec_epoch' < date_to_epoch(month_last_day(toDate('Start')))) then (daysBetween(toDate('Start'),toDate('LastValidDate_sec_epoch'))+1) * 'ForecastedDailyAmount' when ('StartDate_sec_epoch' < 'Start') && ('LastValidDate_sec_epoch' > date_to_epoch(month_last_day(toDate('StartDate_sec_epoch')))) then (daysBetween(toDate('Start'),month_last_day(toDate('Start')))+1) * 'ForecastedDailyAmount' else 'ForecastedAmountFinal' end as 'Forecasted_Amount', 
case when ('StartDate_sec_epoch' >= 'Start') && ('LastValidDate_sec_epoch' > date_to_epoch(month_last_day(toDate('StartDate_sec_epoch')))) then (daysBetween(toDate('StartDate_sec_epoch'),month_last_day(toDate('StartDate_sec_epoch')))+1) * 'ActualDailyAmount' when ('StartDate_sec_epoch' < 'Start') && ('LastValidDate_sec_epoch' < date_to_epoch(month_last_day(toDate('Start')))) then (daysBetween(toDate('Start'),toDate('LastValidDate_sec_epoch'))+1) * 'ActualDailyAmount' when ('StartDate_sec_epoch' < 'Start') && ('LastValidDate_sec_epoch' > date_to_epoch(month_last_day(toDate('StartDate_sec_epoch')))) then (daysBetween(toDate('Start'),month_last_day(toDate('Start')))+1) * 'ActualDailyAmount' else 'DerivedActualAmount' end as 'ActualAmount', 
'ForecastedAmountFinal' as 'ForecastedAmountFinal', 'ForecastedDailyAmount' as 'ForecastedDailyAmount', 'ActualDailyAmount' as 'ActualDailyAmount';
End_Value = foreach valid_slaps generate 'Start' as 'Start', 'End' as 'End', 'Id' as 'Id', 'StartDate_sec_epoch' as 'Start Date', 'LastValidDate_sec_epoch' as 'End Date', case when ('LastValidDate_sec_epoch' < 'End') then date_to_string(toDate('LastValidDate_sec_epoch'),"yyyy") else  date_to_string(toDate('End'),"yyyy") end as 'Date_Year', case when ('LastValidDate_sec_epoch' < 'End') then date_to_string(toDate('LastValidDate_sec_epoch'),"MM") else  date_to_string(toDate('End'),"MM") end as 'Date_Month', 
case 
    when (('StartDate_sec_epoch' < (date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('LastValidDate_sec_epoch'))) - 2764800)))) + 86400)) 
    && ('Start' < (date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('LastValidDate_sec_epoch'))) - 2764800)))) + 86400)) 
    && ('LastValidDate_sec_epoch' < 'End')) then (daysBetween(toDate((date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('LastValidDate_sec_epoch'))) - 2764800)))))),toDate('LastValidDate_sec_epoch'))) * 'ForecastedDailyAmount' 
    when (('StartDate_sec_epoch' < (date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('End'))) - 2764800)))) + 86400)) 
    && ('Start' < (date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('End'))) - 2764800)))) + 86400)) 
    && ('LastValidDate_sec_epoch' > 'End')) then (daysBetween(toDate((date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('End'))) - 2764800)))))),toDate('End'))) * 'ForecastedDailyAmount' 
    else 0 end as 'Forecasted_Amount', 
case 
    when (('StartDate_sec_epoch' < (date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('LastValidDate_sec_epoch'))) - 2764800)))) + 86400)) 
    && ('Start' < (date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('LastValidDate_sec_epoch'))) - 2764800)))) + 86400)) 
    && ('LastValidDate_sec_epoch' < 'End')) then (daysBetween(toDate((date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('LastValidDate_sec_epoch'))) - 2764800)))))),toDate('LastValidDate_sec_epoch'))) * 'ActualDailyAmount' 
    when (('StartDate_sec_epoch' < (date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('End'))) - 2764800)))) + 86400)) 
    && ('Start' < (date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('End'))) - 2764800)))) + 86400)) 
    && ('LastValidDate_sec_epoch' > 'End')) then (daysBetween(toDate((date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('End'))) - 2764800)))))),toDate('End'))) * 'ActualDailyAmount' 
    else 0 end as 'ActualAmount', 'ForecastedAmountFinal' as 'ForecastedAmountFinal', 'ForecastedDailyAmount' as 'ForecastedDailyAmount', 'ActualDailyAmount' as 'ActualDailyAmount';
DataForFill = union Start, End_Value;
DataForFill = foreach DataForFill generate 'Id' as 'Id', 'Date_Year' as 'Date_Year', 'Date_Month' as 'Date_Month','Start' as 'Start', 'End' as 'End', 'Start Date' as 'Start Date', 'End Date' as 'End Date','ActualAmount' as 'ActualAmount', 'Forecasted_Amount' as 'Forecasted_Amount', 'ForecastedAmountFinal' as 'ForecastedAmountFinal', 'ForecastedDailyAmount' as 'ForecastedDailyAmount', 'ActualDailyAmount' as 'ActualDailyAmount';
FilledData = fill DataForFill by (dateCols=('Date_Year', 'Date_Month', "Y-M"), partition='Id');
FilledData = group FilledData by ('Id', 'Date_Year', 'Date_Month');
FilledData = foreach FilledData generate 'Id', 'Date_Year', 'Date_Month', date_to_epoch(toDate('Date_Year'+"-"+'Date_Month'+"-01","yyyy-MM-dd")) as 'Date',first('Start') as 'Start', min(first('Start')) over ([..0] partition by 'Id' order by ('Date_Year','Date_Month')) as 'StartFilter', first('End') as 'End', min(first('End')) over ([..0] partition by 'Id' order by ('Date_Year','Date_Month')) as 'EndFilter',  min(first('Start Date')) over ([..0] partition by 'Id' order by ('Date_Year','Date_Month')) as 'Start Date', min(first('End Date')) over ([..0] partition by 'Id' order by ('Date_Year','Date_Month')) as 'End Date', sum('ActualAmount') as 'ActualAmount', sum('Forecasted_Amount') as 'Forecasted_Amount', min(first('ForecastedDailyAmount'))over ([..0] partition by 'Id' order by ('Date_Year','Date_Month')) as 'ForecastedDailyAmount', min(first('ActualDailyAmount'))over ([..0] partition by 'Id' order by ('Date_Year','Date_Month')) as 'ActualDailyAmount';
FilledData = filter FilledData by coalesce('Start',0) != 0 || ('Date' > 'StartFilter' && 'Date' > 'Start Date' && 'Date' < 'End Date' && 'Date' < 'EndFilter');
FilledData = foreach FilledData generate 'Id', 'Date_Year', 'Date_Month', case when 'Forecasted_Amount' is null then month_days(toDate('Date'))*'ForecastedDailyAmount' else 'Forecasted_Amount' end as 'Forecasted_Amount', case when 'ActualAmount' is null then month_days(toDate('Date'))*'ActualDailyAmount' else 'ActualAmount' end as 'ActualAmount';
result = group FilledData by ('Date_Year', 'Date_Month');
result = foreach result generate 'Date_Year'+"~~~"+'Date_Month' as 'StartDate_Year~~~StartDate_Month', 'Date_Year' as 'StartDate_Year', 'Date_Month' as 'StartDate_Month', "01" as 'StartDate_Day', sum('Forecasted_Amount') as 'Forecasted_Amount', sum('ActualAmount') as 'ActualAmount';
result_cy = filter result by {{row(Time_Toggle_1.selection, [0], ["date_min","date_max"]).asDateRange("date('StartDate_Year','StartDate_Month','StartDate_Day')")}};
result_cy = group result_cy by 'StartDate_Year~~~StartDate_Month';
result_cy = foreach result_cy generate 'StartDate_Year~~~StartDate_Month' as 'StartDate_Year~~~StartDate_Month', coalesce(sum('ActualAmount'),0)*100/sum('Forecasted_Amount') as 'Revenue_Realisation';
result_cy = order result_cy by 'StartDate_Year~~~StartDate_Month' asc;

