nd' && date_to_epoch(now())>= 'End' && 'End' <= date_to_epoch(month_last_day(toDate('Start'))) && 'Start' <= date_to_epoch(now()) then (daysBetween(toDate('Start'), toDate('End'))+1) * 'ActualDailyAmount' when 'StartDate_sec_epoch' < 'Start' &&  'LastValidDate_sec_epoch' >= date_to_epoch(now()) && date_to_epoch(now()) <= date_to_epoch(month_last_day(toDate('Start'))) && 'End' >= date_to_epoch(now()) && 'Start' <= date_to_epoch(now()) then (daysBetween(toDate('Start'), now())+1) * 'ActualDailyAmount' when 'StartDate_sec_epoch' < 'Start' &&  'LastValidDate_sec_epoch' <= date_to_epoch(month_last_day(toDate('Start'))) && date_to_epoch(now()) >= 'LastValidDate_sec_epoch' && 'End' >= 'LastValidDate_sec_epoch' && 'Start' <= date_to_epoch(now()) then (daysBetween(toDate('Start'), toDate('LastValidDate_sec_epoch'))+1) * 'ActualDailyAmount'  else 0 end) as 'ActualAmount'                , 
