 q = foreach q generate 'Id' as 'Id','SalesAgreementProduct.SalesAgreement.Account.Name' as 'SalesAgreementProduct.SalesAgreement.Account.Name', 'StartDate' as 'StartDate', 'ActualEndDate' as 'ActualEndDate', 'DerivedActualAmount' as 'DerivedActualAmount', (case when 'ActualEndDate_sec_epoch'<date_to_epoch(now()) then 'DerivedActualAmount'/(daysBetween(toDate('StartDate', "yyyy-MM-dd"), toDate('ActualEndDate', "yyyy-MM-dd"))+1) else 'DerivedActualAmount'/(daysBetween(toDate('StartDate', "yyyy-MM-dd"), toDate(toString(now(),"yyyy-MM-dd"), "yyyy-MM-dd"))+1) end) as 'ActualDailyAmount', (case when 'ActualEndDate_sec_epoch'<date_to_epoch(now()) or 'StartDate_sec_epoch'>date_to_epoch(now()) then 'ActualEndDate_sec_epoch' else date_to_epoch(now()) end) as 'LastValidDate_sec_epoch', (case when 'ActualEndDate_sec_epoch'<date_to_epoch(now()) or 'StartDate_sec_epoch'>date_to_epoch(now()) then 'ActualEndDate' else toString(now(),"yyyy-MM-dd") end) as 'LastValidDate', 'PlannedDailyAmount' as 'PlannedDailyAmount', 'PlannedAmountFinal' as 'PlannedAmountFinal';
 result = foreach q generate 'Id' as 'Id','SalesAgreementProduct.SalesAgreement.Account.Name' as 'SalesAgreementProduct.SalesAgreement.Account.Name', 'StartDate' as 'StartDate', 'ActualEndDate' as 'ActualEndDate', 
{{cell(Time_Toggle_1.selection, 0, "planned_cal").asObject()}} as 'Planned_Amount', 
{{cell(Time_Toggle_1.selection, 0, "actuals_cal").asObject()}} as 'Actual_Amount';
