valid_slaps = filter valid_slaps by date('ActualEndDate_Year', 'ActualEndDate_Month', 'ActualEndDate_Day') in ["1 year ago"..];
 valid_slaps = foreach valid_slaps generate date_to_epoch(toDate(number_to_string(string_to_number(toString(now(),"yyyy"))-1, "#")    +    "-01-01 00:00:00", "yyyy-MM-dd HH:mm:ss")) as 'Start', date_to_epoch(toDate(toString(now(),"yyyy")    +    "-"    +    toString(month_last_day(now()),"MM-dd ")    +    "23:59:59", "yyyy-MM-dd HH:mm:ss")) as 'End', 'Id' as 'Id', 'StartDate_sec_epoch' as 'StartDate_sec_epoch', 'ActualEndDate_sec_epoch' as 'ActualEndDate_sec_epoch', (case when 'ActualEndDate_sec_epoch'<date_to_epoch(now()) or 'StartDate_sec_epoch'>date_to_epoch(now()) then 'ActualEndDate_sec_epoch' else date_to_epoch(now()) end) as 'LastValidDate_sec_epoch', 'DerivedPlannedAmount' as 'DerivedPlannedAmount', 'PlannedDailyAmount' as 'PlannedDailyAmount', (case when 'ActualEndDate_sec_epoch'<date_to_epoch(now()) then 'DerivedActualAmount'/(daysBetween(toDate('StartDate', "yyyy-MM-dd"), toDate('ActualEndDate', "yyyy-MM-dd"))+1) else 'DerivedActualAmount'/(daysBetween(toDate('StartDate', "yyyy-MM-dd"), toDate(toString(now(),"yyyy-MM-dd"), "yyyy-MM-dd"))+1) end) as 'ActualDailyAmount', 'DerivedActualAmount' as 'DerivedActualAmount';

