Start = foreach valid_slaps generate 'Start' as 'Start', 'End' as 'End', 'Id' as 'Id', 'StartDate_sec_epoch' as 'Start Date', 'ActualEndDate_sec_epoch' as 'End Date', case when ('StartDate_sec_epoch' >= 'Start') then date_to_string(toDate('StartDate_sec_epoch'),"yyyy") else date_to_string(toDate('Start'),"yyyy") end as 'Date_Year', case when ('StartDate_sec_epoch' >= 'Start') then date_to_string(toDate('StartDate_sec_epoch'),"MM") else date_to_string(toDate('Start'),"MM") end as 'Date_Month',                 case when ('StartDate_sec_epoch' >= 'Start') && ('ActualEndDate_sec_epoch' >= date_to_epoch(month_last_day(toDate('StartDate_sec_epoch')))) && 'End' >= date_to_epoch(month_last_day(toDate('StartDate_sec_epoch'))) then (daysBetween(toDate('StartDate_sec_epoch'),month_last_day(toDate('StartDate_sec_epoch')))+1) * 'PlannedDailyAmount' when ('StartDate_sec_epoch' >= 'Start') && ('ActualEndDate_sec_epoch' >= 'End') && 'End' <= date_to_epoch(month_last_day(toDate('StartDate_sec_epoch'))) then (daysBetween(toDate('StartDate_sec_epoch'),toDate('End'))+1) * 'PlannedDailyAmount' when ('StartDate_sec_epoch' < 'Start') && ('ActualEndDate_sec_epoch' >= date_to_epoch(month_last_day(toDate('Start')))) && 'End' >= date_to_epoch(month_last_day(toDate('Start'))) then (daysBetween(toDate('Start'),month_last_day(toDate('Start')))+1) * 'PlannedDailyAmount' when ('StartDate_sec_epoch' < 'Start') && ('ActualEndDate_sec_epoch' <= date_to_epoch(month_last_day(toDate('Start')))) && 'End' >= 'ActualEndDate_sec_epoch' then (daysBetween(toDate('Start'),toDate('ActualEndDate_sec_epoch'))+1) * 'PlannedDailyAmount' when ('StartDate_sec_epoch' < 'Start') && ('End' <= date_to_epoch(month_last_day(toDate('Start')))) && 'End' <= 'ActualEndDate_sec_epoch' then (daysBetween(toDate('Start'),toDate('End'))+1) * 'PlannedDailyAmount' else 'PlannedAmountFinal' end as 'Amount'                , 'PlannedAmountFinal' as 'PlannedAmountFinal', 'PlannedDailyAmount' as 'PlannedDailyAmount';
End_Value = foreach valid_slaps generate 'Start' as 'Start', 'End' as 'End', 'Id' as 'Id', 'StartDate_sec_epoch' as 'Start Date', 'ActualEndDate_sec_epoch' as 'End Date', case when ('ActualEndDate_sec_epoch' < 'End') then date_to_string(toDate('ActualEndDate_sec_epoch'),"yyyy") else  date_to_string(toDate('End'),"yyyy") end as 'Date_Year', case when ('ActualEndDate_sec_epoch' < 'End') then date_to_string(toDate('ActualEndDate_sec_epoch'),"MM") else  date_to_string(toDate('End'),"MM") end as 'Date_Month', case 
    when (('StartDate_sec_epoch' < (date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('ActualEndDate_sec_epoch'))) - 2764800)))) + 86400)) 
    && ('Start' < (date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('ActualEndDate_sec_epoch'))) - 2764800)))) + 86400)) 
    && ('ActualEndDate_sec_epoch' < 'End')) then (daysBetween(toDate((date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('ActualEndDate_sec_epoch'))) - 2764800)))))),toDate('ActualEndDate_sec_epoch'))) * 'PlannedDailyAmount' 
    when (('StartDate_sec_epoch' < (date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('End'))) - 2764800)))) + 86400)) 
    && ('Start' < (date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('End'))) - 2764800)))) + 86400)) 
    && ('ActualEndDate_sec_epoch' > 'End')) then (daysBetween(toDate((date_to_epoch(month_last_day(toDate((date_to_epoch(month_last_day(toDate('End'))) - 2764800)))))),toDate('End'))) * 'PlannedDailyAmount' 
    else 0 end as 'Amount', 'PlannedAmountFinal' as 'PlannedAmountFinal', 'PlannedDailyAmount' as 'PlannedDailyAmount';
DataForFill = union Start, End_Value;
DataForFill = foreach DataForFill generate 'Id' as 'Id', 'Date_Year' as 'Date_Year', 'Date_Month' as 'Date_Month','Start' as 'Start', 'End' as 'End', 'Start Date' as 'Start Date', 'End Date' as 'End Date','Amount' as 'Amount', 'PlannedAmountFinal' as 'PlannedAmountFinal', 'PlannedDailyAmount' as 'PlannedDailyAmount';
FilledData = fill DataForFill by (dateCols=('Date_Year', 'Date_Month', "Y-M"), partition='Id');
FilledData = group FilledData by ('Id', 'Date_Year', 'Date_Month');
FilledData = foreach FilledData generate 'Id', 'Date_Year', 'Date_Month', date_to_epoch(toDate('Date_Year'+"-"+'Date_Month'+"-01","yyyy-MM-dd")) as 'Date',first('Start') as 'Start', min(first('Start')) over ([..0] partition by 'Id' order by ('Date_Year','Date_Month')) as 'StartFilter', first('End') as 'End', min(first('End')) over ([..0] partition by 'Id' order by ('Date_Year','Date_Month')) as 'EndFilter',  min(first('Start Date')) over ([..0] partition by 'Id' order by ('Date_Year','Date_Month')) as 'Start Date', max(first('End Date')) over ([..] partition by 'Id' ) as 'End Date', sum('Amount') as 'Amount', min(first('PlannedDailyAmount'))over ([..0] partition by 'Id' order by ('Date_Year','Date_Month')) as 'PlannedDailyAmount';
FilledData = filter FilledData by coalesce('Start',0) != 0 || ('Date' > 'StartFilter' && 'Date' > 'Start Date' && 'Date' < 'End Date' && 'Date' < 'EndFilter');
FilledData = foreach FilledData generate 'Id', 'Date_Year', 'Date_Month', case when 'Amount' is null then month_days(toDate('Date'))*'PlannedDailyAmount' else 'Amount' end as 'Amount';
result = group FilledData by ('Date_Year', 'Date_Month');
result = foreach result generate 'Date_Year'+"~~~"+'Date_Month' as 'StartDate_Year~~~StartDate_Month', 'Date_Year' as 'StartDate_Year', "01" as 'StartDate_Day', 'Date_Month' as 'StartDate_Month', sum('Amount') as 'Amount';
 result = foreach result generate 'StartDate_Year' + "~~~" + 'StartDate_Month' as 'StartDate_Year~~~StartDate_Month', 'StartDate_Year' as 'StartDate_Year', 'StartDate_Month' as 'StartDate_Month', "01" as 'StartDate_Day', 'Amount' as 'Amount';
result_cy = filter result by {{row(Time_Toggle_1.selection, [0], ["date_min","date_max"]).asDateRange("date('StartDate_Year','StartDate_Month','StartDate_Day')")}};
result_cy = group result_cy by 'StartDate_Year~~~StartDate_Month';
result_cy = foreach result_cy generate 'StartDate_Year~~~StartDate_Month' as 'StartDate_Year~~~StartDate_Month', first('StartDate_Year') as 'StartDate_Year', first('StartDate_Month') as 'StartDate_Month', sum('Amount') as 'Amount', sum(sum('Amount')) over ([..0] partition by all order by 'StartDate_Year~~~StartDate_Month' asc) as 'cumm_Amount';
result_cy = foreach result_cy generate 'StartDate_Year~~~StartDate_Month' as 'StartDate_Year~~~StartDate_Month', 'Amount' as 'Planned_Revenue', 'cumm_Amount' as 'cumm_Planned_Revenue';
result_cy = order result_cy by 'StartDate_Year~~~StartDate_Month' asc;

Forecasted_Revenue_1.query:
q = load "${App.Datasets.MFG_SalesAgreementProductSchedule_tp.FullyQualifiedName}";
q = filter q by 'isValidProductSchedule' == "true";
result = foreach q generate 'Id' as 'Id','SalesAgreementProduct.SalesAgreement.Account.Name' as 'SalesAgreementProduct.SalesAgreement.Account.Name', 'StartDate' as 'StartDate', 'ActualEndDate' as 'ActualEndDate', 
{{cell(Time_Toggle_1.selection, 0, "forecasted_cal").asObject()}} as 'Forecasted_Amount'
, 'ForecastedAmountFinal' as 'A', 'ForecastedDailyAmount' as 'B', daysBetween(toDate('StartDate', "yyyy-MM-dd"), toDate('ActualEndDate', "yyyy-MM-dd")) as 'days', daysBetween(year_last_day( now() ), toDate('ActualEndDate', "yyyy-MM-dd") ) as 'days_to_end';
result = foreach result generate 'Id' as 'Id', 'SalesAgreementProduct.SalesAgreement.Account.Name', 'A', 'B', 'days', 'days_to_end', 'Forecasted_Amount', 'StartDate' as 'StartDate', 'ActualEndDate' as 'ActualEndDate';
result = group result by all;
result = foreach result generate sum('Forecasted_Amount') as 'Forecasted_Amount';
result = order result by ('Forecasted_Amount' asc);
