SalesAgreementProduc_1.query:
q = load "${App.Datasets.MFG_SalesAgreementProductSchedule_tp.FullyQualifiedName}";
result = group q by ('SalesAgreementProduct.SalesAgreement.Account.Name', 'SalesAgreementProduct.Name', 'SalesAgreementProduct.SalesAgreement.Name', 'SalesAgreementProduct.SalesAgreement.Status', 'SalesAgreementProduct.SalesAgreement.StartDate_Year', 'SalesAgreementProduct.SalesAgreement.StartDate_Month', 'SalesAgreementProduct.SalesAgreement.StartDate_Day', 'SalesAgreementProduct.SalesAgreement.EndDate_Year', 'SalesAgreementProduct.SalesAgreement.EndDate_Month', 'SalesAgreementProduct.SalesAgreement.EndDate_Day');
result = foreach result generate q.'SalesAgreementProduct.SalesAgreement.Account.Name' as 'SalesAgreementProduct.SalesAgreement.Account.Name', q.'SalesAgreementProduct.Name' as 'SalesAgreementProduct.Name', q.'SalesAgreementProduct.SalesAgreement.Name' as 'SalesAgreementProduct.SalesAgreement.Name', q.'SalesAgreementProduct.SalesAgreement.StartDate_Year' + "-" + q.'SalesAgreementProduct.SalesAgreement.StartDate_Month' + "-" + q.'SalesAgreementProduct.SalesAgreement.StartDate_Day' as 'Agreement Start Date', q.'SalesAgreementProduct.SalesAgreement.EndDate_Year' + "-" + q.'SalesAgreementProduct.SalesAgreement.EndDate_Month' + "-" + q.'SalesAgreementProduct.SalesAgreement.EndDate_Day'  as 'Agreement End Date', q.'SalesAgreementProduct.SalesAgreement.Status' as 'Status', sum(q.'DerivedActualAmount') 
    as 'Actual Revenue', sum(q.'PlannedAmountFinal') as 'B', sum(q.'ActualQuantity') as 'Actual Quantity', sum(q.'PlannedQuantity') as 'Planned Quantity' ;
result = order result by ('SalesAgreementProduct.SalesAgreement.Account.Name' asc, 'SalesAgreementProduct.Name' asc, 
'SalesAgreementProduct.SalesAgreement.Name' asc, 'Status' asc, 'Agreement Start Date' asc, 'Agreement End Date' asc);

