SalesAgreementProduc_6.query:
q = load "${App.Datasets.MFG_SalesAgreementProductSchedule_tp.FullyQualifiedName}";
r = filter q by {{column(SalesAgreementProduc_3.selection, ["SalesAgreementProduct.Name"]).asEquality("SalesAgreementProduct.Name")}};
s = filter q by {{column(SalesAgreementProduc_4.selection,["SalesAgreementProduct.Name"]).asEquality("SalesAgreementProduct.Name") }};

result = group r by 'SalesAgreementProduct.SalesAgreement.Account.Name' left, s by 'SalesAgreementProduct.SalesAgreement.Account.Name';
final = foreach result generate r['SalesAgreementProduct.SalesAgreement.Account.Name'] as 'Account', sum(r.'ActualQuantity') as 'Units of Selected Product', sum(s.'ActualQuantity') as 'Units of Recommended Product';

final = order final by ('Units of Selected Product' desc);

AccountProductRevenu_1.query:
