q = filter q by {{column(SalesAgreementProduc_2.selection, ["SalesAgreementProduct.SalesAgreement.StatusCode"]).asEquality("SalesAgreementProduct.SalesAgreement.StatusCode")}};
p = group p by ('Product.Name', 'SalesAgreement.Account.Name');
p = foreach p generate p.'Product.Name' as 'Product.Name', p.'SalesAgreement.Account.Name' as 'SalesAgreement.Account.Name',
         0 as 'Revenue', 0 as 'ActualQuantity';

q = filter q by 'isValidProductSchedule' == "true";
