q = foreach q generate 'SalesAgreementProduct.SalesAgreement.Account.Name' as 'SalesAgreementProduct.SalesAgreement.Account.Name', 'SalesAgreementProduct.Name' 
    as 'SalesAgreementProduct.Name', sum('DerivedActualAmount') as 'Revenue', sum(ActualQuantity) as 'ActualQuantity';

