r = foreach r generate p.'SalesAgreement.Account.Name' as 'SalesAgreementProduct.SalesAgreement.Account.Name', 
   p.'Product.Name' as 'SalesAgreementProduct.Name',  coalesce(sum(q.'{{row(static_2.selection,[0],["value"]).asObject()}}'),0) as 'Revenue';   
