Relationship_Header_1.query:
q = load "${App.Datasets.MFG_SalesAgreementProductSchedule_tp.FullyQualifiedName}";
q = filter q by {{column(ActiveAccounts_1.result, ["AccountId"]).asEquality('SalesAgreementProduct.SalesAgreement.AccountId')}};
q = foreach q generate q.'SalesAgreementProduct.SalesAgreementId' as 'SalesAgreementProduct.SalesAgreementId',q.'SalesAgreementProduct.SalesAgreement.Name' as 'SalesAgreementProduct.SalesAgreement.Name', q.'SalesAgreementProduct.SalesAgreement.AccountId' as 'AccountId', 'SalesAgreementProduct.SalesAgreement.Account.Name' as 'AccountName', q.'SalesAgreementProduct.SalesAgreement.StartDate_sec_epoch' as 'StartDate_sec_epoch', q.'SalesAgreementProduct.SalesAgreement.EndDate_sec_epoch' as 'EndDate_sec_epoch', 'DerivedActualAmount' as 'DerivedActualAmount', 'PlannedAmountFinal' as 'PlannedAmountFinal', 'DerivedPlannedAmount' as 'DerivedPlannedAmount',
q.'DerivedActualAmount'/(daysBetween(toDate(q.'SalesAgreementProduct.SalesAgreement.StartDate_sec_epoch'),case when toDate(q.'ActualEndDate_sec_epoch') < now() then toDate(q.'ActualEndDate_sec_epoch') else now() end)+1) as 'ActualDailyAmount', 
(case when toDate(q.'SalesAgreementProduct.SalesAgreement.StartDate_sec_epoch') > now() then 0 else (daysBetween((case when toDate(q.'SalesAgreementProduct.SalesAgreement.StartDate_sec_epoch') < toDate((toString(now(), "yyyy")+"-01-01"), "yyyy-MM-dd") && toDate(q.'EndDate_sec_epoch') > toDate((toString(now(), "yyyy")+"-01-01"), "yyyy-MM-dd") then toDate((toString(now(), "yyyy")+"-01-01"), "yyyy-MM-dd") else toDate(q.'SalesAgreementProduct.SalesAgreement.StartDate_sec_epoch') end),case when toDate(q.'ActualEndDate_sec_epoch') < now() then toDate(q.'ActualEndDate_sec_epoch') else now() end)+1) end) as 'ActualDays_YTD', 
q.'PlannedDailyAmount' as 'PlannedDailyAmount',
    case when toDate(q.'ActualEndDate_sec_epoch') > now() then  daysBetween( case when toDate(q.'SalesAgreementProduct.SalesAgreement.StartDate_sec_epoch') > now() then toDate(q.'SalesAgreementProduct.SalesAgreement.StartDate_sec_epoch') else now() end, toDate(q.'ActualEndDate_sec_epoch'))+1 end as 'FutureDays';


q = foreach q generate q.'SalesAgreementProduct.SalesAgreementId' as 'SalesAgreementProduct.SalesAgreementId', q.'SalesAgreementProduct.SalesAgreement.Name' as 'SalesAgreementProduct.SalesAgreement.Name', 'AccountName' as 'AccountName','AccountId' as 'AccountId', 'StartDate_sec_epoch' as 'StartDate_sec_epoch', 'EndDate_sec_epoch' as 'EndDate_sec_epoch', 
'DerivedActualAmount' as 'DerivedActualAmount', 'PlannedAmountFinal' as 'PlannedAmountFinal', 'DerivedPlannedAmount' as 'DerivedPlannedAmount', (q.'ActualDailyAmount'*q.'ActualDays_YTD') as  'RevenueTillToday', (q.'PlannedDailyAmount'*q.'ActualDays_YTD') as 'PlannedTillToday', (q.'PlannedDailyAmount'*q.'FutureDays') as 'PlannedAfterToday'; 

result = group q by all;
result = foreach result generate unique('AccountId') as 'NoOfAccounts', sum(q.'RevenueTillToday')+(sum(q.'PlannedAfterToday')* (sum(q.'RevenueTillToday')/sum(q.PlannedTillToday))) as 'RevenueGenerated', sum(q.'DerivedPlannedAmount') as 'PlannedRevenue', (sum(q.'RevenueTillToday')+(sum(q.'PlannedAfterToday')* (sum(q.'RevenueTillToday')/sum(q.PlannedTillToday))))/unique('AccountId') as 'Avg_Customer_Lifetime_Value';

